# -*- coding: utf-8 -*-import feedparser as fp, refrom 代码_3_3_Python获取RSS格式的数据 import mainprocess # 导入方法def getreviews(bids, uids):# 适当修改此方法	f = fp.parse('http://www.douban.com/feed/people/%s/reviews' % (uids))	for j in f['items']:		item_link = j.link		if item_link.startswith('http://book.douban.com/review/'):			item_des = j.description			bid_re = re.search('book.douban.com/subject/(.+?)/', item_des)			bid = bid_re.group(1)			result_list.append([bids, uids, bid, deep_int])##记录深度# 判断深度循环模块def deepprocess(bids, deep_r):	global deep_int	while 1:		if deep_int == 0:			deep_int += 1			mainprocess(bids)		elif deep_int < deep_r:			deep_int += 1			for k in result_list:				if k[3] == deep_int - 1: mainprocess(k[2])		else: breakdeep_int = 0 ##result_list = []deepprocess('4718495', 2)# 设定深度print(result_list)